import React from "react";
import { Link } from "react-router-dom";
import { 
  UserPlus, 
  Users, 
  FilePlus, 
  FileSearch, 
  BarChart2, 
  Settings, 
  KeyRound, 
  ClipboardList 
} from "lucide-react";

export default function AdminDashboard() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-800 p-8 text-white">
      
      {/* Header */}
      <div className="flex justify-between items-center mb-10">
        <h1 className="text-4xl font-bold tracking-wide">Admin Dashboard</h1>

        <div className="bg-white/20 px-5 py-2 rounded-full backdrop-blur shadow-md cursor-pointer hover:bg-white/30">
          Welcome, Admin ðŸ‘‹
        </div>
      </div>

      {/* GRID MENU */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">

        {/* Add Service */}
        <DashboardCard
          title="Add Service"
          icon={<FilePlus size={40} />}
          link="/add-service"
        />

        {/* Add Client */}
        <DashboardCard
          title="Add Client"
          icon={<UserPlus size={40} />}
          link="/add-client"
        />

        {/* Client List */}
        <DashboardCard
          title="Client List"
          icon={<Users size={40} />}
          link="/client-list"
        />

        {/* Create Invoice */}
        <DashboardCard
          title="Create Invoice"
          icon={<ClipboardList size={40} />}
          link="/invoice"
        />

        {/* Generate Reports */}
        <DashboardCard
          title="Generate Reports"
          icon={<BarChart2 size={40} />}
          link="/reports"
        />

        {/* Search Invoice */}
        <DashboardCard
          title="Search Invoice"
          icon={<FileSearch size={40} />}
          link="/search-invoice"
        />

        {/* Update Profile */}
        <DashboardCard
          title="Update Profile"
          icon={<Settings size={40} />}
          link="/update-profile"
        />

        {/* Change Password */}
        <DashboardCard
          title="Change Password"
          icon={<KeyRound size={40} />}
          link="/change-password"
        />

        {/* Manage all clients */}
        <DashboardCard
          title="Manage All Clients (CRUD)"
          icon={<Users size={40} />}
          link="/manage-clients"
        />

      </div>
    </div>
  );
}

/* Reusable Card Component */
function DashboardCard({ title, icon, link }) {
  return (
    <Link
      to={link}
      className="bg-white/10 hover:bg-white/20 p-8 rounded-3xl shadow-xl 
                 backdrop-blur-md text-center font-semibold cursor-pointer 
                 transition-all duration-300 transform hover:-translate-y-2 
                 hover:shadow-2xl flex flex-col items-center gap-4"
    >
      <div className="text-white">{icon}</div>
      <p className="text-xl">{title}</p>
    </Link>
  );
}
