import React from "react";
import { Link } from "react-router-dom";

// Simple dashboard layout matching login theme (blue gradient)
export default function AdminDashboard() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-blue-800 p-6 text-white">
      <h1 className="text-3xl font-bold mb-8">Admin Dashboard</h1>

      {/* GRID MENU */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Add Service */}
        <Link to="/add-service" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Add Service
        </Link>

        {/* Add Client */}
        <Link to="/add-client" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Add Client
        </Link>

        {/* Client List */}
        <Link to="/client-list" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Client List
        </Link>

        {/* Invoice */}
        <Link to="/invoice" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Create Invoice
        </Link>

        {/* Generate Reports */}
        <Link to="/reports" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Generate Reports
        </Link>

        {/* Search Invoice */}
        <Link to="/search-invoice" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Search Invoice
        </Link>

        {/* Update Profile */}
        <Link to="/update-profile" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Update Profile
        </Link>

        {/* Admin Change Password */}
        <Link to="/change-password" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Change Password
        </Link>

        {/* CRUD All Clients */}
        <Link to="/manage-clients" className="bg-white/10 hover:bg-white/20 p-6 rounded-2xl shadow-md backdrop-blur text-center font-semibold">
          Manage All Clients (CRUD)
        </Link>
      </div>
    </div>
  );
}
